export default class Keystore {
    static KEYSTORE_LOCATION: string;
    private static DEFAULT_ENCODING_OPTIONS;
    private static DEFAULT_INMEMORY_ENCODING_OPTIONS;
    private static _instance;
    private db;
    private constructor();
    /**
     * Opens Level DB Keystore<string, CryptoKey>
     * Falls back to in-memory Level if backing storage implementation (e.g. indexeddb) is missing
     * @returns Keystore instance
     */
    static instance(): Promise<Keystore>;
    static persitentInstance(): Promise<Keystore>;
    static inMemoryInstance(): Promise<Keystore>;
    store(keyName: string, key: CryptoKey): Promise<void>;
    get(keyName: string): Promise<CryptoKey>;
    delete(keyName: string): Promise<void>;
    close(): Promise<void>;
}
